      /*--------------------------------------------------------------------------------------+
      |                Reload all pages and merge the results into one table.                 |
      +--------------------------------------------------------------------------------------*/

$versionnumber = (' V'+GM_info.script.version+'');															// Current version, based on @version
$youruserid = $(".username").attr("href");																	// Your profile href
$authkey = CURRENT_USER.authKey;																			// Your authorization key
if ($(".pagenums").length < 1 ){$("h2").after('<br><br>');};												// Fill the space left by the missing page numbers o_O
$(".thin").after('<div id="loading" style="display:none; \
										   position:fixed; \
										   width:100px; \
										   height:30px; \
										   margin:-15px -50px; \
										   top:30%; \
										   opacity:1; \
										   left:50%; \
										   z-index:1010"> \
										   <h2>Loading...</h2> \
				  </div>');																					// Loading...
$(document).ajaxStart(function(){																			// Show curtain until ajax request finishes
	$("#curtain,#loading").show();
}).ajaxStop(function(){
	$("#curtain,#loading").hide();
});

      /*--------------------------------------------------------------------------------------+
      |              If ajaxtimer is set to true, merge the pages available.                  |
      +--------------------------------------------------------------------------------------*/
	  
if (localStorage.getItem('ajaxreload') == 'true'){															// If you pressed OK to perform ajax reload
	localStorage.setItem('timer','10');																		// Set timer to 10 minutes
	localStorage.setItem('ajaxreload','false');																// Set ajaxreload back to false
	$lastpagenum = $("a.next-prev.last").attr("href").match(/\d+/);											// Page number of the last page
	$(".pagenums").replaceWith('<br>');																		// Replace page numbers with line break (I don't have to reposition some stuff)
	for($nextpage = 2; $nextpage <= $lastpagenum; $nextpage++){												// Load all pages with ajax
		$.ajax({
			url:      "https://animebytes.tv/forums.php?page="+$nextpage+"&action=viewunread",
			async:    false,
			dataType: 'html',
			success:  function(data){
				$(data).find("tbody").each(function(){														// Find tbody in currently loaded html
					$("tbody").append($(this).html());														// Append it to the tbody
					$(".colhead").not(":first-of-type").remove();											// Remove .colhead except the first one
				});
			}
		});
	};
	console.log("AJAX reload was succesful.");	
};

      /*--------------------------------------------------------------------------------------+
      |                 Default values set in localStorage on the first run.                  |
      +--------------------------------------------------------------------------------------*/

if(!localStorage.getItem('timer')) { localStorage.setItem('timer','2');};						// Timer value set to 2 minutes on first run.
if(!localStorage.getItem('opacity')) { localStorage.setItem('opacity','0.45');};				// Timer opacity set to 0.45 on first run.
if(!localStorage.getItem('forumlinks')) { localStorage.setItem('forumlinks','false');};			// Forum URL options set to false on first run.
if(!localStorage.getItem('threadlinks')) { localStorage.setItem('threadlinks','false');};		// Thread URL options set to false on first run.
if(!localStorage.getItem('profilelinks')) { localStorage.setItem('profilelinks','false');};		// Profile URL options set to false on first run.
if(!localStorage.getItem('ajaxreload')) { localStorage.setItem('ajaxreload','false');};			// Set AJAX reload to false on first run.

      /*--------------------------------------------------------------------------------------+
      |                  Figuring out which stylesheet is used by the user.                   |
      +--------------------------------------------------------------------------------------*/
	  
if ($("link[href*='coalbytes']").length > 0){$curr_css = 'coalbytes';}							// Searching for the active stylesheet set by the user
else if ($("link[href*='tentacletastic']").length > 0){$curr_css = 'tentacletastic';}			//        S
else if ($("link[href*='gorgeous']").length > 0){$curr_css = 'gorgeous';}						//          O
else if ($("link[href*='milkyway']").length > 0){$curr_css = 'milkyway';}						//                  W
else if ($("link[href*='toblerone']").length > 0){$curr_css = 'toblerone';}						//                    H
else if ($("link[href*='dream']").length > 0){$curr_css = 'dream';}								//                      A
else if ($("link[href*='white']").length > 0){$curr_css = 'white';}								//                        T
else if ($("link[href*='black']").length > 0){$curr_css = 'black';};							//                          ?
