      /*--------------------------------------------------------------------------------------+
      |  Forum icons, BG for Announcements, and glow effect for threads older than one week.  |
      +--------------------------------------------------------------------------------------*/

/* Threads older than one week - Red Glow */ $("td:contains('week'),td:contains('weeks'),td:contains('month'),td:contains('months')").parent().children(':nth-child(3)').children().css({'text-shadow':'0 0 3px #FF0000, 0 0 3px #FF0000'});
/* Announcements - BG [Red Gradient] */ $("td:first-child:contains('Announcements')").parent().children(':first-child').css({'background': 'linear-gradient(to right,rgba(255,0,0,0.15),rgba(0,0,0,0))'});
